import { useEffect, useState } from 'react';
import axios from 'axios';

export default function AttendanceDashboard() {
  const [records, setRecords] = useState([]);
  const [filteredRecords, setFilteredRecords] = useState([]);
  const [q, setQ] = useState('');
  const [date, setDate] = useState('');
  const [loading, setLoading] = useState(true);
  const [stats, setStats] = useState({ present: 0, absent: 0, total: 0 });

  const fetchData = async () => {
    try {
      setLoading(true);
      const params = {};
      if (q) params.q = q;
      if (date) params.date = date;
      const res = await axios.get('http://localhost:5000/api/attendance', { params });
      setRecords(res.data);
      calculateStats(res.data);
    } catch (err) {
      console.error(err);
      alert('Error fetching records.');
    } finally {
      setLoading(false);
    }
  };

  const calculateStats = (data) => {
    const present = data.filter(record => record.status === 'Present').length;
    const absent = data.filter(record => record.status === 'Absent').length;
    setStats({
      present,
      absent,
      total: data.length
    });
  };

  useEffect(() => {
    fetchData();
    const handler = () => fetchData();
    window.addEventListener('attendance:added', handler);
    return () => window.removeEventListener('attendance:added', handler);
  }, []);

  useEffect(() => {
    filterRecords();
  }, [records, q, date]);

  const filterRecords = () => {
    let filtered = records;

    if (q) {
      filtered = filtered.filter(record =>
        record.employeeName.toLowerCase().includes(q.toLowerCase()) ||
        record.employeeID.toLowerCase().includes(q.toLowerCase())
      );
    }

    if (date) {
      filtered = filtered.filter(record => record.date === date);
    }

    setFilteredRecords(filtered);
    calculateStats(filtered);
  };

  const handleDelete = async (id) => {
    if (!confirm('Are you sure you want to delete this record?')) return;
    try {
      await axios.delete(`http://localhost:5000/api/attendance/${id}`);
      setRecords(records => records.filter(x => x.id !== id));
    } catch (err) {
      console.error(err);
      alert('Error deleting record.');
    }
  };

  const clearFilters = () => {
    setQ('');
    setDate('');
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  };

  const getStatusBadge = (status) => {
    return status === 'Present' 
      ? <span className="badge bg-success">Present</span>
      : <span className="badge bg-danger">Absent</span>;
  };

  if (loading) {
    return (
      <div className="d-flex justify-content-center align-items-center" style={{ height: '400px' }}>
        <div className="text-center">
          <div className="spinner-border text-primary" style={{ width: '3rem', height: '3rem' }} role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p className="mt-3 text-muted">Loading attendance records...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="container-fluid">
      {/* Header Section */}
      <div className="row mb-4">
        <div className="col-12">
          <div className="d-flex justify-content-between align-items-center">
            <div>
              <h2 className="h4 fw-bold text-dark mb-1">Attendance Dashboard</h2>
              <p className="text-muted mb-0">Manage and monitor employee attendance records</p>
            </div>
            <button 
              className="btn btn-outline-primary"
              onClick={fetchData}
            >
              <i className="bi bi-arrow-clockwise me-2"></i>
              Refresh
            </button>
          </div>
        </div>
      </div>

      {/* Statistics Cards */}
      <div className="row mb-4">
        <div className="col-md-4">
          <div className="card border-0 shadow-sm">
            <div className="card-body">
              <div className="d-flex align-items-center">
                <div className="flex-shrink-0">
                  <div className="bg-primary bg-opacity-10 p-3 rounded">
                    <i className="bi bi-people-fill text-primary fs-4"></i>
                  </div>
                </div>
                <div className="flex-grow-1 ms-3">
                  <h6 className="card-title text-muted mb-1">Total Records</h6>
                  <h3 className="fw-bold text-dark mb-0">{stats.total}</h3>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className="col-md-4">
          <div className="card border-0 shadow-sm">
            <div className="card-body">
              <div className="d-flex align-items-center">
                <div className="flex-shrink-0">
                  <div className="bg-success bg-opacity-10 p-3 rounded">
                    <i className="bi bi-check-circle-fill text-success fs-4"></i>
                  </div>
                </div>
                <div className="flex-grow-1 ms-3">
                  <h6 className="card-title text-muted mb-1">Present</h6>
                  <h3 className="fw-bold text-dark mb-0">{stats.present}</h3>
                </div>
              </div>
            </div>
          </div>
        </div>
        <div className="col-md-4">
          <div className="card border-0 shadow-sm">
            <div className="card-body">
              <div className="d-flex align-items-center">
                <div className="flex-shrink-0">
                  <div className="bg-danger bg-opacity-10 p-3 rounded">
                    <i className="bi bi-x-circle-fill text-danger fs-4"></i>
                  </div>
                </div>
                <div className="flex-grow-1 ms-3">
                  <h6 className="card-title text-muted mb-1">Absent</h6>
                  <h3 className="fw-bold text-dark mb-0">{stats.absent}</h3>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Filters Card */}
      <div className="card border-0 shadow-sm mb-4">
        <div className="card-header bg-transparent py-3">
          <h5 className="card-title mb-0 fw-semibold">
            <i className="bi bi-funnel me-2"></i>
            Filters & Search
          </h5>
        </div>
        <div className="card-body">
          <div className="row g-3">
            <div className="col-md-5">
              <label htmlFor="searchInput" className="form-label fw-semibold">Search Employees</label>
              <div className="input-group">
                <span className="input-group-text bg-light border-end-0">
                  <i className="bi bi-search text-muted"></i>
                </span>
                <input
                  id="searchInput"
                  type="text"
                  className="form-control border-start-0"
                  placeholder="Search by name or employee ID..."
                  value={q}
                  onChange={e => setQ(e.target.value)}
                />
              </div>
            </div>
            <div className="col-md-4">
              <label htmlFor="dateFilter" className="form-label fw-semibold">Filter by Date</label>
              <div className="input-group">
                <span className="input-group-text bg-light border-end-0">
                  <i className="bi bi-calendar text-muted"></i>
                </span>
                <input
                  id="dateFilter"
                  type="date"
                  className="form-control border-start-0"
                  value={date}
                  onChange={e => setDate(e.target.value)}
                />
              </div>
            </div>
            <div className="col-md-3 d-flex align-items-end">
              <div className="d-flex gap-2 w-100">
                <button 
                  className="btn btn-primary flex-fill"
                  onClick={fetchData}
                >
                  <i className="bi bi-funnel me-2"></i>
                  Apply Filters
                </button>
                <button 
                  className="btn btn-outline-secondary"
                  onClick={clearFilters}
                  disabled={!q && !date}
                >
                  <i className="bi bi-x-circle"></i>
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>

      {/* Records Table Card */}
      <div className="card border-0 shadow-sm">
        <div className="card-header bg-transparent py-3">
          <div className="d-flex justify-content-between align-items-center">
            <h5 className="card-title mb-0 fw-semibold">
              <i className="bi bi-list-check me-2"></i>
              Attendance Records
            </h5>
            <span className="badge bg-primary bg-opacity-10 text-primary fs-6">
              {filteredRecords.length} {filteredRecords.length === 1 ? 'Record' : 'Records'}
            </span>
          </div>
        </div>
        <div className="card-body p-0">
          {filteredRecords.length === 0 ? (
            <div className="text-center py-5">
              <i className="bi bi-inbox display-1 text-muted"></i>
              <h5 className="text-muted mt-3">No records found</h5>
              <p className="text-muted">
                {records.length === 0 
                  ? "No attendance records available. Start by adding some records." 
                  : "No records match your current filters."}
              </p>
              {(q || date) && (
                <button className="btn btn-primary mt-2" onClick={clearFilters}>
                  Clear Filters
                </button>
              )}
            </div>
          ) : (
            <div className="table-responsive">
              <table className="table table-hover align-middle mb-0">
                <thead className="bg-light">
                  <tr>
                    <th className="ps-4 fw-semibold text-muted border-0">Employee</th>
                    <th className="fw-semibold text-muted border-0">Employee ID</th>
                    <th className="fw-semibold text-muted border-0">Date</th>
                    <th className="fw-semibold text-muted border-0">Status</th>
                    <th className="fw-semibold text-muted border-0 text-end pe-4">Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredRecords.map(record => (
                    <tr key={record.id} className="border-top">
                      <td className="ps-4">
                        <div className="d-flex align-items-center">
                          <div className="bg-primary bg-opacity-10 rounded-circle p-2 me-3">
                            <i className="bi bi-person-fill text-primary"></i>
                          </div>
                          <div>
                            <h6 className="mb-0 fw-semibold">{record.employeeName}</h6>
                          </div>
                        </div>
                      </td>
                      <td>
                        <span className="text-muted">{record.employeeID}</span>
                      </td>
                      <td>
                        <span className="fw-medium">{formatDate(record.date)}</span>
                      </td>
                      <td>
                        {getStatusBadge(record.status)}
                      </td>
                      <td className="text-end pe-4">
                        <button
                          className="btn btn-sm btn-outline-danger"
                          onClick={() => handleDelete(record.id)}
                          title="Delete record"
                        >
                          <i className="bi bi-trash me-1"></i>
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>

      {/* Add Bootstrap Icons CSS */}
      <link 
        rel="stylesheet" 
        href="https://cdn.jsdelivr.net/npm/bootstrap-icons@1.11.1/font/bootstrap-icons.css" 
      />
    </div>
  );
}
