import React, { useState, useEffect } from 'react';
import axios from 'axios';

const AttendanceDashboard = () => {
  const [attendance, setAttendance] = useState([]);
  const [filteredAttendance, setFilteredAttendance] = useState([]);
  const [loading, setLoading] = useState(true);
  const [message, setMessage] = useState('');
  const [searchTerm, setSearchTerm] = useState('');
  const [dateFilter, setDateFilter] = useState('');

  useEffect(() => {
    fetchAttendance();
  }, []);

  useEffect(() => {
    filterAttendance();
  }, [attendance, searchTerm, dateFilter]);

  const fetchAttendance = async () => {
    try {
      setLoading(true);
      const response = await axios.get('/api/attendance');
      setAttendance(response.data);
    } catch (error) {
      setMessage('Error fetching attendance records');
      console.error('Error:', error);
    } finally {
      setLoading(false);
    }
  };

  const filterAttendance = () => {
    let filtered = [...attendance];

    // Apply search filter
    if (searchTerm) {
      filtered = filtered.filter(record =>
        record.employeeName.toLowerCase().includes(searchTerm.toLowerCase()) ||
        record.employeeID.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // Apply date filter
    if (dateFilter) {
      filtered = filtered.filter(record => record.date === dateFilter);
    }

    setFilteredAttendance(filtered);
  };

  const handleDelete = async (id) => {
    if (window.confirm('Are you sure you want to delete this record?')) {
      try {
        await axios.delete(`/api/attendance/${id}`);
        setMessage('Record deleted successfully');
        fetchAttendance(); // Refresh the list
      } catch (error) {
        setMessage('Error deleting record');
        console.error('Error:', error);
      }
    }
  };

  const clearFilters = () => {
    setSearchTerm('');
    setDateFilter('');
  };

  if (loading) {
    return (
      <div className="d-flex justify-content-center">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  return (
    <div>
      <div className="card">
        <div className="card-header bg-primary text-white">
          <h4 className="mb-0">Attendance Records</h4>
        </div>
        <div className="card-body">
          {message && (
            <div className="alert alert-info">
              {message}
            </div>
          )}

          {/* Filters */}
          <div className="row mb-3">
            <div className="col-md-4">
              <input
                type="text"
                className="form-control"
                placeholder="Search by name or ID..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
              />
            </div>
            <div className="col-md-4">
              <input
                type="date"
                className="form-control"
                value={dateFilter}
                onChange={(e) => setDateFilter(e.target.value)}
              />
            </div>
            <div className="col-md-4">
              <button 
                className="btn btn-outline-secondary w-100"
                onClick={clearFilters}
              >
                Clear Filters
              </button>
            </div>
          </div>

          {/* Attendance Table */}
          {filteredAttendance.length === 0 ? (
            <div className="alert alert-warning text-center">
              No attendance records found.
            </div>
          ) : (
            <div className="table-responsive">
              <table className="table table-striped table-hover">
                <thead className="table-dark">
                  <tr>
                    <th>Employee Name</th>
                    <th>Employee ID</th>
                    <th>Date</th>
                    <th>Status</th>
                    <th>Actions</th>
                  </tr>
                </thead>
                <tbody>
                  {filteredAttendance.map(record => (
                    <tr key={record.id}>
                      <td>{record.employeeName}</td>
                      <td>{record.employeeID}</td>
                      <td>{new Date(record.date).toLocaleDateString()}</td>
                      <td>
                        <span className={record.status === 'Present' ? 'present-badge' : 'absent-badge'}>
                          {record.status}
                        </span>
                      </td>
                      <td>
                        <button
                          className="btn btn-danger btn-sm"
                          onClick={() => handleDelete(record.id)}
                        >
                          Delete
                        </button>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}

          <div className="mt-3 text-muted">
            Showing {filteredAttendance.length} of {attendance.length} records
          </div>
        </div>
      </div>
    </div>
  );
};

export default AttendanceDashboard;
