import { useState } from 'react';
import axios from 'axios';

export default function AttendanceForm(){
  const [employeeName, setEmployeeName] = useState('');
  const [employeeID, setEmployeeID] = useState('');
  const [status, setStatus] = useState('Present');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!employeeName.trim() || !employeeID.trim()) {
      alert('Please fill in name and ID.');
      return;
    }
    setLoading(true);
    const date = new Date().toISOString().split('T')[0];
    try {
      await axios.post('http://localhost:5000/api/attendance', {
        employeeName, employeeID, date, status
      });
      alert('Attendance recorded.');
      setEmployeeName('');
      setEmployeeID('');
      setStatus('Present');
      window.dispatchEvent(new Event('attendance:added'));
    } catch (err) {
      console.error(err);
      alert('Error saving attendance.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="card">
      <div className="card-body">
        <h5 className="card-title">Mark Attendance</h5>
        <form onSubmit={handleSubmit}>
          <div className="mb-2">
            <label className="form-label">Employee Name</label>
            <input className="form-control" value={employeeName} onChange={e=>setEmployeeName(e.target.value)} />
          </div>
          <div className="mb-2">
            <label className="form-label">Employee ID</label>
            <input className="form-control" value={employeeID} onChange={e=>setEmployeeID(e.target.value)} />
          </div>
          <div className="mb-3">
            <label className="form-label">Status</label>
            <select className="form-select" value={status} onChange={e=>setStatus(e.target.value)}>
              <option>Present</option>
              <option>Absent</option>
            </select>
          </div>
          <button className="btn btn-primary" type="submit" disabled={loading}>
            {loading ? 'Saving...' : 'Submit'}
          </button>
        </form>
      </div>
    </div>
  );
}
